sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate) {
		var rightnow = new GlideDateTime();
		var testdatetime = new GlideDateTime(strDate);
		if(testdatetime.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},

	isDateToday: function(strDate) {
		var rightnow = new GlideDateTime();
		var testdatetime = new GlideDateTime(strDate);
		var dayToday = rightnow.getLocalDate();
		var testDate = testdatetime.getLocalDate();

		if(dayToday.compareTo(testDate) == 0) {
			return true;
		}
		else {
			return false;
		}
	},

    type: 'NeedItUtils'
};
